import { useCallback } from 'react';
import cardFlipSound from '../assets/sounds/card-flip.mp3';
import confirmSound from '../assets/sounds/confirm.mp3';

export function useSound() {
  const playCardSound = useCallback(() => {
    const audio = new Audio(cardFlipSound);
    audio.volume = 0.5;
    audio.play();
  }, []);

  const playConfirmSound = useCallback(() => {
    const audio = new Audio(confirmSound);
    audio.volume = 0.3;
    audio.play();
  }, []);

  return {
    playCardSound,
    playConfirmSound
  };
}
