import React from 'react';
import { useNavigate, useLocation, useParams } from 'react-router-dom';

function Room() {
  const navigate = useNavigate();
  const location = useLocation();
  const { roomId } = useParams();
  const [selectedCard, setSelectedCard] = React.useState<string | null>(null);
  const [isStarted, setIsStarted] = React.useState(false);
  const [time, setTime] = React.useState(0);

  const username = location.state?.username;
  const participantCount = location.state?.participantCount || 0;
  const isHost = location.state?.isHost || false;

  React.useEffect(() => {
    if (!username) {
      navigate('/');
    }
  }, [username, navigate]);

  React.useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isStarted) {
      interval = setInterval(() => {
        setTime(prev => prev + 1);
      }, 1000);
    }
    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [isStarted]);

  const handleCardSelect = (card: string) => {
    setSelectedCard(card === selectedCard ? null : card);
  };

  const handleLogout = () => {
    navigate('/', { replace: true });
  };

  const formatTime = (seconds: number): string => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const cards = ['1', '2', '3', '5', '8', '13', '21', '34', '?'];

  return (
    <div className="min-h-screen bg-gray-50 py-6 px-4 sm:px-6 lg:px-8">
      <div className="max-w-7xl mx-auto">
        <div className="flex justify-between items-center mb-8">
          <div>
            <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
              Scrum Poker
            </h1>
            <p className="text-gray-600">Oda: {roomId}</p>
            <p className="text-gray-600">Katılımcı: {username}</p>
            <p className="text-gray-600">Toplam Katılımcı: {participantCount}</p>
          </div>

          {isStarted && (
            <div className="text-2xl font-mono bg-white shadow rounded-lg p-4">
              {formatTime(time)}
            </div>
          )}

          <button
            onClick={handleLogout}
            className="px-4 py-2 border border-transparent text-base font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
          >
            Ana Sayfaya Dön
          </button>
        </div>

        {isHost && !isStarted && (
          <button
            onClick={() => setIsStarted(true)}
            className="mb-8 px-6 py-3 border border-transparent text-lg font-medium rounded-xl text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
          >
            Eforlamayı Başlat
          </button>
        )}

        <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-9 gap-4">
          {cards.map((card) => (
            <button
              key={card}
              onClick={() => handleCardSelect(card)}
              className={`
                aspect-[2/3] flex items-center justify-center text-2xl font-bold rounded-xl shadow-lg
                transform transition-all duration-150 hover:scale-105
                ${selectedCard === card
                  ? 'bg-blue-600 text-white'
                  : 'bg-white text-gray-900 hover:bg-gray-50'
                }
              `}
            >
              {card}
            </button>
          ))}
        </div>

        {selectedCard && (
          <div className="mt-8 p-4 bg-white rounded-xl shadow">
            <p className="text-lg">
              Seçilen kart: <span className="font-bold">{selectedCard}</span>
            </p>
          </div>
        )}
      </div>
    </div>
  );
}

export default Room;
