import React from 'react';
import { useNavigate, useLocation, useParams } from 'react-router-dom';

interface Participant {
  id: string;
  username: string;
  selectedCard: string | null;
}

function Room() {
  const navigate = useNavigate();
  const location = useLocation();
  const { roomId } = useParams();
  const [selectedCard, setSelectedCard] = React.useState<string | null>(null);
  const [isStarted, setIsStarted] = React.useState(false);
  const [time, setTime] = React.useState(0);
  const [participants, setParticipants] = React.useState<Participant[]>([]);

  const username = location.state?.username;
  const participantCount = location.state?.participantCount || 0;
  const isHost = location.state?.isHost || false;

  React.useEffect(() => {
    if (!username) {
      navigate('/');
    } else {
      // Kendimizi katılımcı olarak ekliyoruz
      setParticipants([
        {
          id: '1',
          username: username,
          selectedCard: null
        }
      ]);
    }
  }, [username, navigate]);

  React.useEffect(() => {
    let interval: NodeJS.Timeout;
    if (isStarted) {
      interval = setInterval(() => {
        setTime(prev => prev + 1);
      }, 1000);
    }
    return () => {
      if (interval) {
        clearInterval(interval);
      }
    };
  }, [isStarted]);

  const handleCardSelect = (card: string) => {
    setSelectedCard(card === selectedCard ? null : card);
    // Kendi seçimimizi katılımcılar listesinde güncelliyoruz
    setParticipants(prev => prev.map(p => 
      p.username === username 
        ? { ...p, selectedCard: card === selectedCard ? null : card }
        : p
    ));
  };

  const handleLogout = () => {
    navigate('/', { replace: true });
  };

  const formatTime = (seconds: number): string => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const cards = ['1', '2', '3', '5', '8', '13', '21', '34', '?'];

  return (
    <div className="min-h-screen bg-gray-50 py-4 px-2 sm:py-6 sm:px-4">
      <div className="max-w-7xl mx-auto">
        {/* Header Section */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 mb-6 sm:mb-8">
          <div>
            <h1 className="text-2xl sm:text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
              Scrum Poker
            </h1>
            <div className="mt-2 space-y-1">
              <p className="text-sm sm:text-base text-gray-600">Oda: {roomId}</p>
              <p className="text-sm sm:text-base text-gray-600">Katılımcı: {username}</p>
              <p className="text-sm sm:text-base text-gray-600">Toplam Katılımcı: {participantCount}</p>
            </div>
          </div>

          <div className="flex items-center gap-4 w-full sm:w-auto">
            {isStarted && (
              <div className="text-xl sm:text-2xl font-mono bg-white shadow rounded-lg p-3 sm:p-4 flex-1 sm:flex-none text-center">
                {formatTime(time)}
              </div>
            )}

            <button
              onClick={handleLogout}
              className="px-4 py-2 border border-transparent text-sm sm:text-base font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 flex-1 sm:flex-none"
            >
              Ana Sayfaya Dön
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex flex-col lg:grid lg:grid-cols-3 gap-4 sm:gap-8">
          {/* Cards Section */}
          <div className="lg:col-span-2 space-y-4 sm:space-y-8 order-2 lg:order-1">
            {isHost && !isStarted && (
              <button
                onClick={() => setIsStarted(true)}
                className="w-full px-4 sm:px-6 py-3 border border-transparent text-base sm:text-lg font-medium rounded-xl text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
              >
                Eforlamayı Başlat
              </button>
            )}

            <div className="grid grid-cols-3 sm:grid-cols-4 md:grid-cols-6 lg:grid-cols-9 gap-2 sm:gap-4">
              {cards.map((card) => (
                <button
                  key={card}
                  onClick={() => handleCardSelect(card)}
                  className={`
                    aspect-[2/3] flex items-center justify-center text-xl sm:text-2xl font-bold rounded-xl shadow-lg
                    transform transition-all duration-150 hover:scale-105
                    ${selectedCard === card
                      ? 'bg-blue-600 text-white'
                      : 'bg-white text-gray-900 hover:bg-gray-50'
                    }
                  `}
                >
                  {card}
                </button>
              ))}
            </div>
          </div>

          {/* Participants Section */}
          <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6 order-1 lg:order-2">
            <h2 className="text-lg sm:text-xl font-semibold mb-4">Katılımcılar</h2>
            <div className="space-y-3 sm:space-y-4">
              {participants.map((participant) => (
                <div 
                  key={participant.id}
                  className="flex items-center justify-between p-2 sm:p-3 bg-gray-50 rounded-lg"
                >
                  <div className="flex items-center space-x-2 sm:space-x-3">
                    <div className="w-7 h-7 sm:w-8 sm:h-8 bg-blue-600 rounded-full flex items-center justify-center text-white font-semibold text-sm sm:text-base">
                      {participant.username.charAt(0).toUpperCase()}
                    </div>
                    <span className="font-medium text-sm sm:text-base">{participant.username}</span>
                  </div>
                  {participant.selectedCard && (
                    <span className="px-2 sm:px-3 py-1 bg-blue-100 text-blue-800 rounded-full font-medium text-sm sm:text-base">
                      {participant.selectedCard}
                    </span>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Room;
