import React, { useState, useEffect } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';

interface LocationState {
  roomId: string;
  isHost: boolean;
}

function Join() {
  const location = useLocation();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [participantCount, setParticipantCount] = useState(1);
  const [error, setError] = useState('');

  const state = location.state as LocationState;
  const roomId = state?.roomId;
  const isHost = state?.isHost;

  useEffect(() => {
    if (!roomId) {
      navigate('/');
    }
  }, [roomId, navigate]);

  const handleJoin = () => {
    if (!username.trim()) {
      setError('Lütfen kullanıcı adınızı girin');
      return;
    }

    if (isHost && participantCount < 1) {
      setError('Katılımcı sayısı en az 1 olmalıdır');
      return;
    }

    navigate('/room', {
      state: {
        roomId,
        username: username.trim(),
        isHost,
        participantCount
      }
    });
  };

  return (
    <div className="min-h-screen flex items-center justify-center px-4 sm:px-6 lg:px-8">
      <div className="max-w-md w-full space-y-8">
        <div className="text-center">
          <h1 className="text-3xl sm:text-4xl font-bold text-gray-900">
            Toplantıya Katıl
          </h1>
          <p className="mt-2 text-sm sm:text-base text-gray-600">
            Toplantı ID: <span className="font-mono font-medium">{roomId}</span>
          </p>
        </div>

        <div className="mt-8 space-y-6">
          <div className="space-y-4">
            <div>
              <label htmlFor="username" className="block text-sm font-medium text-gray-700">
                Kullanıcı Adı
              </label>
              <input
                id="username"
                type="text"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
                className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                placeholder="Kullanıcı adınızı girin"
              />
            </div>

            {isHost && (
              <div>
                <label htmlFor="participantCount" className="block text-sm font-medium text-gray-700">
                  Katılımcı Sayısı
                </label>
                <input
                  id="participantCount"
                  type="number"
                  min="1"
                  value={participantCount}
                  onChange={(e) => setParticipantCount(Math.max(1, parseInt(e.target.value) || 1))}
                  className="mt-1 block w-full px-3 py-2 border border-gray-300 rounded-md shadow-sm focus:outline-none focus:ring-blue-500 focus:border-blue-500 sm:text-sm"
                />
              </div>
            )}
          </div>

          {error && (
            <div className="text-red-600 text-sm">{error}</div>
          )}

          <button
            onClick={handleJoin}
            className="w-full flex justify-center py-2 px-4 border border-transparent rounded-md shadow-sm text-sm font-medium text-white bg-blue-600 hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            Toplantıya Katıl
          </button>
        </div>
      </div>
    </div>
  );
}

export default Join;
