import React, { useEffect, useState, useCallback } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import { Card } from '../components/Card';
import { ParticipantList } from '../components/ParticipantList';
import { Notification } from '../components/Notification';
import { RoomHeader } from '../components/RoomHeader';
import { RoomControls } from '../components/RoomControls';

interface LocationState {
  roomId: string;
  username: string;
  isHost: boolean;
  participantCount: number;
}

function Room() {
  const location = useLocation();
  const navigate = useNavigate();
  const { socket, isConnected } = useSocket();
  const [participants, setParticipants] = useState<Array<{ id: string; username: string; vote?: number }>>([]);
  const [notifications, setNotifications] = useState<Array<{ id: number; message: string; type: 'success' | 'error' }>>([]);
  const [isVoting, setIsVoting] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [selectedCard, setSelectedCard] = useState<number | null>(null);
  const [isHost, setIsHost] = useState(false);
  const [username, setUsername] = useState('');
  const [roomId, setRoomId] = useState('');

  useEffect(() => {
    const state = location.state as LocationState;
    if (!state?.roomId || !state?.username) {
      navigate('/');
      return;
    }

    setRoomId(state.roomId);
    setUsername(state.username);
    setIsHost(state.isHost);
  }, [location, navigate]);

  const addNotification = useCallback((message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, message, type }]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 3000);
  }, []);

  useEffect(() => {
    if (!socket || !roomId || !username) return;

    // Odaya katılma
    if (!isConnected) {
      socket.emit('joinRoom', { roomId, username });
    }

    // Katılımcı listesi güncelleme
    const handleParticipantsUpdate = (updatedParticipants: Array<{ id: string; username: string; vote?: number }>) => {
      setParticipants(updatedParticipants);
    };

    // Yeni katılımcı bildirimi
    const handleNewParticipant = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıya katıldı`, 'success');
    };

    // Katılımcı ayrılma bildirimi
    const handleParticipantLeft = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıdan ayrıldı`, 'error');
    };

    // Oylama başlatma
    const handleVotingStart = () => {
      setIsVoting(true);
      setShowResults(false);
      setSelectedCard(null);
      addNotification('Oylama başladı!', 'success');
    };

    // Oylama sonuçları
    const handleVotingResults = (results: Array<{ username: string; vote: number }>) => {
      setIsVoting(false);
      setShowResults(true);
      setParticipants(prev => 
        prev.map(p => {
          const result = results.find(r => r.username === p.username);
          return result ? { ...p, vote: result.vote } : p;
        })
      );
      addNotification('Oylama tamamlandı!', 'success');
    };

    // Event listener'ları ekle
    socket.on('participantsUpdate', handleParticipantsUpdate);
    socket.on('newParticipant', handleNewParticipant);
    socket.on('participantLeft', handleParticipantLeft);
    socket.on('votingStart', handleVotingStart);
    socket.on('votingResults', handleVotingResults);

    // Cleanup
    return () => {
      socket.off('participantsUpdate', handleParticipantsUpdate);
      socket.off('newParticipant', handleNewParticipant);
      socket.off('participantLeft', handleParticipantLeft);
      socket.off('votingStart', handleVotingStart);
      socket.off('votingResults', handleVotingResults);
    };
  }, [socket, roomId, username, isConnected, addNotification]);

  const handleVote = useCallback((value: number) => {
    if (!socket || !isVoting) return;

    setSelectedCard(value);
    socket.emit('vote', { roomId, username, vote: value });
  }, [socket, isVoting, roomId, username]);

  const handleStartVoting = useCallback(() => {
    if (!socket || !isHost) return;
    socket.emit('startVoting', { roomId });
  }, [socket, isHost, roomId]);

  const handleShowResults = useCallback(() => {
    if (!socket || !isHost) return;
    socket.emit('showResults', { roomId });
  }, [socket, isHost, roomId]);

  const handleResetVoting = useCallback(() => {
    if (!socket || !isHost) return;
    socket.emit('resetVoting', { roomId });
  }, [socket, isHost, roomId]);

  const handleLeaveRoom = useCallback(() => {
    if (socket) {
      socket.emit('leaveRoom', { roomId, username });
    }
    navigate('/');
  }, [socket, roomId, username, navigate]);

  const cards = [1, 2, 3, 5, 8, 13, 21, 34, 55];

  return (
    <div className="min-h-screen bg-gray-100 py-6 sm:py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="fixed top-4 right-4 z-50 space-y-2">
          {notifications.map(notification => (
            <Notification
              key={notification.id}
              message={notification.message}
              type={notification.type}
            />
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Header */}
          <div className="lg:col-span-3">
            <RoomHeader
              roomId={roomId}
              username={username}
              isHost={isHost}
            />
          </div>

          {/* Cards Section */}
          <div className="lg:col-span-2 space-y-4 sm:space-y-8">
            <div className="grid grid-cols-3 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-9 gap-2 sm:gap-3">
              {cards.map((card) => (
                <Card
                  key={card}
                  value={card}
                  isSelected={selectedCard === card}
                  onSelect={handleVote}
                />
              ))}
            </div>

            <RoomControls
              isVoting={isVoting}
              isHost={isHost}
              onStartVoting={handleStartVoting}
              onShowResults={handleShowResults}
              onResetVoting={handleResetVoting}
              onLeaveRoom={handleLeaveRoom}
            />
          </div>

          {/* Participants Section */}
          <div className="lg:col-span-1">
            <ParticipantList
              participants={participants}
              isHost={isHost}
              onStartVoting={handleStartVoting}
              onShowResults={handleShowResults}
              onResetVoting={handleResetVoting}
              onLeaveRoom={handleLeaveRoom}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

export default Room;
