import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';

function Home() {
  const navigate = useNavigate();
  const [roomId, setRoomId] = useState('');

  const handleCreateRoom = () => {
    const newRoomId = Math.random().toString(36).substring(2, 8).toUpperCase();
    navigate('/join', { 
      state: { 
        roomId: newRoomId,
        isHost: true
      }
    });
  };

  const handleJoinRoom = () => {
    if (roomId.trim()) {
      navigate('/join', { 
        state: { 
          roomId: roomId.trim().toUpperCase(),
          isHost: false
        }
      });
    } else {
      alert('LÃ¼tfen oda kodunu girin');
    }
  };

  return (
    <div className="min-h-screen bg-gray-50 flex items-center justify-center py-12 px-4 sm:px-6 lg:px-8">
      <div className="relative">
        {/* Arka plan kartÄ± */}
        <div className="absolute -top-4 -right-4 w-full h-full bg-blue-600 rounded-3xl transform rotate-3"></div>

        {/* Ana kart */}
        <div className="relative bg-white rounded-3xl shadow-xl p-8 sm:p-12 w-full max-w-md">
          <div className="flex flex-col items-center space-y-8">
            {/* Logo ve BaÅlÄ±k */}
            <div className="flex items-center space-x-4">
              <div className="relative">
                <div className="absolute inset-0 bg-blue-600 rounded-lg transform rotate-6"></div>
                <div className="relative bg-blue-500 text-white w-12 h-12 flex items-center justify-center text-2xl font-bold rounded-lg shadow-lg">
                  5
                </div>
              </div>
              <h1 className="text-3xl font-bold text-gray-900">
                Scrum Poker
              </h1>
            </div>

            {/* Form */}
            <div className="w-full space-y-6">
              {/* ToplantÄ± OluÅtur Butonu */}
              <button
                onClick={handleCreateRoom}
                className="w-full flex items-center justify-center px-8 py-3 text-base font-medium text-white bg-blue-600 rounded-xl hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transform transition-transform duration-150 hover:scale-105"
              >
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M12 4v16m8-8H4" />
                </svg>
                ToplantÄ± OluÅtur
              </button>

              {/* AyÄ±rÄ±cÄ± */}
              <div className="flex items-center w-full">
                <div className="flex-1 h-px bg-gray-200"></div>
                <span className="px-4 text-sm text-gray-500">veya</span>
                <div className="flex-1 h-px bg-gray-200"></div>
              </div>

              <div>
                <label htmlFor="roomId" className="block text-sm font-medium text-gray-700">
                  Oda Kodu
                </label>
                <input
                  type="text"
                  id="roomId"
                  value={roomId}
                  onChange={(e) => setRoomId(e.target.value.toUpperCase())}
                  placeholder="Oda kodunu girin"
                  className="mt-1 block w-full px-4 py-3 bg-gray-50 border border-gray-300 rounded-xl shadow-sm focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                />
              </div>
              <button
                onClick={handleJoinRoom}
                className="w-full flex items-center justify-center px-8 py-3 text-base font-medium text-white bg-green-600 rounded-xl hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 transform transition-transform duration-150 hover:scale-105"
              >
                <svg className="w-5 h-5 mr-2" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                  <path strokeLinecap="round" strokeLinejoin="round" strokeWidth={2} d="M13 7l5 5m0 0l-5 5m5-5H6" />
                </svg>
                Odaya KatÄ±l
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Home;
