import React from 'react';

interface RoomHeaderProps {
  roomId: string;
  username: string;
  isHost: boolean;
  teamName?: string;
}

export const RoomHeader: React.FC<RoomHeaderProps> = ({
  roomId,
  username,
  isHost,
  teamName,
}) => {
  const handleCopyRoomId = () => {
    navigator.clipboard.writeText(roomId);
    alert('Toplantı ID kopyalandı!');
  };

  return (
    <div className="bg-white rounded-xl shadow-lg p-6 mb-6">
      <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4">
        <div className="space-y-1">
          <div className="flex items-center gap-2">
            <h1 className="text-2xl font-bold text-gray-900">Oda: {roomId}</h1>
            <span className="px-2 py-1 text-xs font-medium bg-blue-100 text-blue-800 rounded-full">
              {isHost ? 'Moderatör' : 'Katılımcı'}
            </span>
          </div>
          <p className="text-gray-600">Kullanıcı: {username}</p>
          {teamName && (
            <p className="text-gray-600">Ekip: {teamName}</p>
          )}
        </div>
        <div className="flex items-center gap-2">
          <span className="text-sm text-gray-600">Toplantı ID:</span>
          <button
            onClick={handleCopyRoomId}
            className="px-3 py-1 text-sm font-medium text-blue-600 bg-blue-50 rounded-md hover:bg-blue-100 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
          >
            {roomId}
          </button>
        </div>
      </div>
    </div>
  );
};
