import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import Room from './components/Room';

function App() {
  const location = useLocation();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [roomId, setRoomId] = useState('');
  const [participantCount, setParticipantCount] = useState(0);
  const [showParticipantInput, setShowParticipantInput] = useState(false);
  const [showUsernameInput, setShowUsernameInput] = useState(false);

  const handleCreateRoom = () => {
    const roomId = Math.random().toString(36).substring(2, 8).toUpperCase();
    setShowParticipantInput(true);
    setRoomId(roomId);
  };

  const handleConfirmParticipants = () => {
    if (participantCount > 0) {
      navigate(`/room/${roomId}`, { 
        state: { 
          username: username,
          participantCount: participantCount,
          isHost: true
        } 
      });
    }
  };

  const handleJoinRoom = () => {
    if (roomId && username) {
      navigate(`/room/${roomId}`, { 
        state: { 
          username: username,
          participantCount: 0, // Bu değer backend'den gelecek
          isHost: false
        } 
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200">
      <Routes>
        <Route path="/" element={
          <div className="max-w-7xl mx-auto py-6 sm:px-6 lg:px-8">
            <div className="text-center">
              <h1 className="text-4xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600 mb-8">
                Scrum Poker
              </h1>
              {!showParticipantInput && !showUsernameInput && (
                <div className="space-y-4">
                  <button
                    onClick={handleCreateRoom}
                    className="w-full max-w-md px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200 text-lg font-medium"
                  >
                    Toplantı Oluştur
                  </button>
                  <div className="relative my-4">
                    <div className="absolute inset-0 flex items-center">
                      <div className="w-full border-t border-gray-300"></div>
                    </div>
                    <div className="relative flex justify-center text-sm">
                      <span className="px-2 bg-gray-100 text-gray-500">veya</span>
                    </div>
                  </div>
                  <div className="space-y-4">
                    <input
                      type="text"
                      placeholder="Toplantı ID'si"
                      value={roomId}
                      onChange={(e) => setRoomId(e.target.value)}
                      className="w-full max-w-md px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                    />
                    <button
                      onClick={() => setShowUsernameInput(true)}
                      className="w-full max-w-md px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors duration-200 text-lg font-medium"
                    >
                      Toplantıya Katıl
                    </button>
                  </div>
                </div>
              )}
              {showParticipantInput && (
                <div className="max-w-md mx-auto space-y-4">
                  <h2 className="text-xl font-semibold text-gray-800">Katılımcı Sayısı</h2>
                  <input
                    type="number"
                    min="1"
                    value={participantCount}
                    onChange={(e) => setParticipantCount(parseInt(e.target.value) || 0)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                  <button
                    onClick={handleConfirmParticipants}
                    disabled={participantCount < 1}
                    className="w-full px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200 text-lg font-medium disabled:bg-gray-400 disabled:cursor-not-allowed"
                  >
                    Devam Et
                  </button>
                </div>
              )}
              {showUsernameInput && (
                <div className="max-w-md mx-auto space-y-4">
                  <h2 className="text-xl font-semibold text-gray-800">Kullanıcı Adı</h2>
                  <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                  <button
                    onClick={handleJoinRoom}
                    disabled={!username.trim()}
                    className="w-full px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors duration-200 text-lg font-medium disabled:bg-gray-400 disabled:cursor-not-allowed"
                  >
                    Toplantıya Katıl
                  </button>
                </div>
              )}
            </div>
          </div>
        } />
        <Route 
          path="/room/:roomId" 
          element={
            <Room 
              participantCount={location.state?.participantCount || 0} 
              isHost={location.state?.isHost || false} 
            />
          } 
        />
      </Routes>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
