import React, { useEffect, useState, useCallback } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import { Card } from '../components/Card';
import { ParticipantList } from '../components/ParticipantList';
import { Notification } from '../components/Notification';
import { RoomHeader } from '../components/RoomHeader';
import { RoomControls } from '../components/RoomControls';

interface LocationState {
  roomId: string;
  username: string;
  isHost: boolean;
  participantCount: number;
}

interface EffortItem {
  id: string;
  title: string;
  description: string;
  votes: Array<{ username: string; vote: number }>;
  status: 'pending' | 'voting' | 'completed';
}

function Room() {
  const location = useLocation();
  const navigate = useNavigate();
  const { socket, isConnected } = useSocket();
  const [participants, setParticipants] = useState<Array<{ id: string; username: string; vote?: number }>>([]);
  const [notifications, setNotifications] = useState<Array<{ id: number; message: string; type: 'success' | 'error' }>>([]);
  const [isVoting, setIsVoting] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [selectedCard, setSelectedCard] = useState<number | null>(null);
  const [isHost, setIsHost] = useState(false);
  const [username, setUsername] = useState('');
  const [roomId, setRoomId] = useState('');
  const [effortItems, setEffortItems] = useState<EffortItem[]>([]);
  const [currentEffortItem, setCurrentEffortItem] = useState<EffortItem | null>(null);
  const [newItemTitle, setNewItemTitle] = useState('');
  const [newItemDescription, setNewItemDescription] = useState('');

  useEffect(() => {
    const state = location.state as LocationState;
    if (!state?.roomId || !state?.username) {
      navigate('/');
      return;
    }

    setRoomId(state.roomId);
    setUsername(state.username);
    setIsHost(state.isHost);
  }, [location, navigate]);

  const addNotification = useCallback((message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, message, type }]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 3000);
  }, []);

  useEffect(() => {
    if (!socket || !roomId || !username) return;

    // Odaya katılma
    if (!isConnected) {
      socket.emit('joinRoom', { roomId, username });
    }

    // Katılımcı listesi güncelleme
    const handleParticipantsUpdate = (updatedParticipants: Array<{ id: string; username: string; vote?: number }>) => {
      setParticipants(updatedParticipants);
    };

    // Yeni katılımcı bildirimi
    const handleNewParticipant = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıya katıldı`, 'success');
    };

    // Katılımcı ayrılma bildirimi
    const handleParticipantLeft = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıdan ayrıldı`, 'error');
    };

    // Eforlama başlatma
    const handleVotingStart = (itemId: string) => {
      setIsVoting(true);
      setShowResults(false);
      setSelectedCard(null);
      setCurrentEffortItem(effortItems.find(item => item.id === itemId) || null);
      addNotification('Eforlama başladı!', 'success');
    };

    // Eforlama sonuçları
    const handleVotingResults = (itemId: string, results: Array<{ username: string; vote: number }>) => {
      setIsVoting(false);
      setShowResults(true);
      setEffortItems(prev => 
        prev.map(item => {
          if (item.id === itemId) {
            return {
              ...item,
              votes: results,
              status: 'completed'
            };
          }
          return item;
        })
      );
      addNotification('Eforlama tamamlandı!', 'success');

      // Bir sonraki bekleyen eforlama öğesini seç
      const nextPendingItem = effortItems.find(item => item.status === 'pending');
      if (nextPendingItem) {
        setCurrentEffortItem(nextPendingItem);
        addNotification('Bir sonraki eforlama öğesine geçildi', 'success');
      }
    };

    // Yeni eforlama öğesi
    const handleNewEffortItem = (item: EffortItem) => {
      setEffortItems(prev => [...prev, item]);
      // İlk eforlama öğesini otomatik seç
      if (!currentEffortItem) {
        setCurrentEffortItem(item);
      }
      addNotification('Yeni eforlama öğesi eklendi!', 'success');
    };

    // Event listener'ları ekle
    socket.on('participantsUpdate', handleParticipantsUpdate);
    socket.on('newParticipant', handleNewParticipant);
    socket.on('participantLeft', handleParticipantLeft);
    socket.on('votingStart', handleVotingStart);
    socket.on('votingResults', handleVotingResults);
    socket.on('newEffortItem', handleNewEffortItem);

    // Cleanup
    return () => {
      socket.off('participantsUpdate', handleParticipantsUpdate);
      socket.off('newParticipant', handleNewParticipant);
      socket.off('participantLeft', handleParticipantLeft);
      socket.off('votingStart', handleVotingStart);
      socket.off('votingResults', handleVotingResults);
      socket.off('newEffortItem', handleNewEffortItem);
    };
  }, [socket, roomId, username, isConnected, addNotification, effortItems, currentEffortItem]);

  // İlk eforlama öğesini seç
  useEffect(() => {
    if (effortItems.length > 0 && !currentEffortItem) {
      const firstItem = effortItems[0];
      setCurrentEffortItem(firstItem);
    }
  }, [effortItems, currentEffortItem]);

  const handleVote = useCallback((value: number) => {
    if (!socket || !username || !isVoting || !currentEffortItem) return;

    setSelectedCard(value);

    // Katılımcıların oylarını güncelle
    setParticipants(prev => 
      prev.map(p => 
        p.username === username ? { ...p, vote: value } : p
      )
    );

    socket.emit('vote', { 
      roomId, 
      username, 
      vote: value,
      itemId: currentEffortItem.id
    });
  }, [socket, username, isVoting, roomId, currentEffortItem]);

  const handleStartVoting = useCallback(() => {
    if (!socket || !isHost) return;

    // Aktif eforlama öğesini bul
    const activeItem = effortItems.find(item => item.status === 'pending');
    if (!activeItem) return;

    setIsVoting(true);
    socket.emit('startVoting', { roomId, itemId: activeItem.id });
  }, [socket, isHost, roomId, effortItems]);

  const handleShowResults = useCallback(() => {
    if (!socket || !isHost) return;

    // Aktif eforlama öğesini bul
    const activeItem = effortItems.find(item => item.status === 'voting');
    if (!activeItem) return;

    setIsVoting(false);
    socket.emit('showResults', { roomId, itemId: activeItem.id });
  }, [socket, isHost, roomId, effortItems]);

  const handleResetVoting = useCallback(() => {
    if (!socket || !isHost) return;

    // Aktif eforlama öğesini bul
    const activeItem = effortItems.find(item => item.status === 'voting');
    if (!activeItem) return;

    setIsVoting(false);
    socket.emit('resetVoting', { roomId, itemId: activeItem.id });
  }, [socket, isHost, roomId, effortItems]);

  const handleAddEffortItem = useCallback(() => {
    if (!socket || !isHost || !newItemTitle.trim()) return;

    const newItem: EffortItem = {
      id: Date.now().toString(),
      title: newItemTitle.trim(),
      description: newItemDescription.trim(),
      votes: [],
      status: 'pending'
    };

    socket.emit('addEffortItem', { roomId, item: newItem });
    setNewItemTitle('');
    setNewItemDescription('');
  }, [socket, isHost, roomId, newItemTitle, newItemDescription]);

  const handleLeaveRoom = useCallback(() => {
    if (socket) {
      socket.emit('leaveRoom', { roomId, username });
    }
    navigate('/');
  }, [socket, roomId, username, navigate]);

  const cards = [1, 2, 3, 5, 8, 13, 21, 34, 55];

  return (
    <div className="min-h-screen bg-gray-100 py-6 sm:py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="fixed top-4 right-4 z-50 space-y-2">
          {notifications.map(notification => (
            <Notification
              key={notification.id}
              message={notification.message}
              type={notification.type}
            />
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Header */}
          <div className="lg:col-span-3">
            <RoomHeader
              roomId={roomId}
              username={username}
              isHost={isHost}
            />
          </div>

          {/* Cards Section */}
          <div className="lg:col-span-3 space-y-4 sm:space-y-8">
            {/* Mevcut Eforlama Öğesi */}
            {currentEffortItem && (
              <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6">
                <div className="flex items-center justify-between mb-4">
                  <h3 className="text-lg sm:text-xl font-semibold text-gray-900">
                    {currentEffortItem.title}
                  </h3>
                  <span className={`
                    px-3 py-1 text-sm rounded-full
                    ${currentEffortItem.status === 'pending' ? 'bg-yellow-100 text-yellow-800' : ''}
                    ${currentEffortItem.status === 'voting' ? 'bg-blue-100 text-blue-800' : ''}
                    ${currentEffortItem.status === 'completed' ? 'bg-green-100 text-green-800' : ''}
                  `}>
                    {currentEffortItem.status === 'pending' ? 'Bekliyor' : ''}
                    {currentEffortItem.status === 'voting' ? 'Oylama' : ''}
                    {currentEffortItem.status === 'completed' ? 'Tamamlandı' : ''}
                  </span>
                </div>
                {currentEffortItem.description && (
                  <p className="mt-2 text-gray-600">
                    {currentEffortItem.description}
                  </p>
                )}
              </div>
            )}

            {/* Katılımcı Listesi - Sadece moderatör için */}
            {isHost && (
              <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6">
                <h3 className="text-lg sm:text-xl font-semibold text-gray-900 mb-4">
                  Katılımcılar
                </h3>
                <ParticipantList participants={participants} />
              </div>
            )}

            {/* Fibonacci Kartları - Sadece katılımcılar için */}
            {!isHost && (
              <div className="grid grid-cols-3 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-9 gap-2 sm:gap-3">
                {cards.map((card) => (
                  <Card
                    key={card}
                    value={card}
                    isSelected={selectedCard === card}
                    onSelect={handleVote}
                  />
                ))}
              </div>
            )}

            <RoomControls
              isVoting={isVoting}
              isHost={isHost}
              onStartVoting={handleStartVoting}
              onShowResults={handleShowResults}
              onResetVoting={handleResetVoting}
              onLeaveRoom={handleLeaveRoom}
            />
          </div>
        </div>
      </div>
    </div>
  );
}

