import React, { useEffect, useState, useCallback } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import { Card } from '../components/Card';
import { ParticipantList } from '../components/ParticipantList';
import { Notification } from '../components/Notification';
import { RoomHeader } from '../components/RoomHeader';
import { RoomControls } from '../components/RoomControls';

interface LocationState {
  roomId: string;
  username: string;
  isHost: boolean;
  participantCount: number;
}

function Room() {
  const location = useLocation();
  const navigate = useNavigate();
  const { socket, isConnected } = useSocket();
  const [participants, setParticipants] = useState<Array<{ id: string; username: string; vote?: number }>>([]);
  const [notifications, setNotifications] = useState<Array<{ id: number; message: string; type: 'success' | 'error' }>>([]);
  const [isVoting, setIsVoting] = useState(false);
  const [showResults, setShowResults] = useState(false);
  const [selectedCard, setSelectedCard] = useState<number | null>(null);
  const [isHost, setIsHost] = useState(false);
  const [username, setUsername] = useState('');
  const [roomId, setRoomId] = useState('');
  const [participantCount, setParticipantCount] = useState(0);

  useEffect(() => {
    const state = location.state as LocationState;
    if (!state?.roomId || !state?.username) {
      navigate('/');
      return;
    }

    setRoomId(state.roomId);
    setUsername(state.username);
    setIsHost(state.isHost);
    setParticipantCount(state.participantCount);
  }, [location, navigate]);

  useEffect(() => {
    if (!socket || !roomId || !username) return;

    // Odaya katılma
    if (!isConnected) {
      socket.emit('joinRoom', { roomId, username });
    }

    // Katılımcı listesi güncelleme
    const handleParticipantsUpdate = (updatedParticipants: Array<{ id: string; username: string; vote?: number }>) => {
      setParticipants(updatedParticipants);
    };

    // Yeni katılımcı bildirimi
    const handleNewParticipant = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıya katıldı`, 'success');
    };

    // Katılımcı ayrılma bildirimi
    const handleParticipantLeft = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıdan ayrıldı`, 'error');
    };

    // Oylama başlatma
    const handleVotingStart = () => {
      setIsVoting(true);
      setShowResults(false);
      setSelectedCard(null);
      addNotification('Oylama başladı!', 'success');
    };

    // Oylama sonuçları
    const handleVotingResults = (results: Array<{ username: string; vote: number }>) => {
      setIsVoting(false);
      setShowResults(true);
      setParticipants(prev => 
        prev.map(p => {
          const result = results.find(r => r.username === p.username);
          return result ? { ...p, vote: result.vote } : p;
        })
      );
      addNotification('Oylama tamamlandı!', 'success');
    };

    // Event listener'ları ekle
    socket.on('participantsUpdate', handleParticipantsUpdate);
    socket.on('newParticipant', handleNewParticipant);
    socket.on('participantLeft', handleParticipantLeft);
    socket.on('votingStart', handleVotingStart);
    socket.on('votingResults', handleVotingResults);

    // Cleanup
    return () => {
      socket.off('participantsUpdate', handleParticipantsUpdate);
      socket.off('newParticipant', handleNewParticipant);
      socket.off('participantLeft', handleParticipantLeft);
      socket.off('votingStart', handleVotingStart);
      socket.off('votingResults', handleVotingResults);
    };
  }, [socket, roomId, username, isConnected]);

  const addNotification = useCallback((message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, message, type }]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 3000);
  }, []);

  const handleVote = useCallback((value: number) => {
    if (!socket || !isVoting) return;

    setSelectedCard(value);
    socket.emit('vote', { roomId, username, vote: value });
  }, [socket, isVoting, roomId, username]);

  const handleStartVoting = useCallback(() => {
    if (!socket || !isHost) return;
    socket.emit('startVoting', { roomId });
  }, [socket, isHost, roomId]);

  const handleShowResults = useCallback(() => {
    if (!socket || !isHost) return;
    socket.emit('showResults', { roomId });
  }, [socket, isHost, roomId]);

  const handleResetVoting = useCallback(() => {
    if (!socket || !isHost) return;
    socket.emit('resetVoting', { roomId });
  }, [socket, isHost, roomId]);

  const handleLeaveRoom = useCallback(() => {
    if (socket) {
      socket.emit('leaveRoom', { roomId, username });
    }
    navigate('/');
  }, [socket, roomId, username, navigate]);

  const formatTime = (seconds: number): string => {
    const minutes = Math.floor(seconds / 60);
    const remainingSeconds = seconds % 60;
    return `${minutes}:${remainingSeconds.toString().padStart(2, '0')}`;
  };

  const cards = ['1', '2', '3', '5', '8', '13', '21', '34', '?'];

  return (
    <div className="min-h-screen bg-gray-50 py-4 px-2 sm:py-6 sm:px-4">
      {/* Bildirimler */}
      <div className="fixed top-4 right-4 z-50 space-y-2">
        {notifications.map(notification => (
          <Notification
            key={notification.id}
            message={notification.message}
            type={notification.type}
          />
        ))}
      </div>

      <div className="max-w-7xl mx-auto">
        {/* Header Section */}
        <div className="flex flex-col sm:flex-row justify-between items-start sm:items-center gap-4 mb-6 sm:mb-8">
          <div>
            <h1 className="text-2xl sm:text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
              Scrum Poker
            </h1>
            <div className="mt-2 space-y-1">
              <p className="text-sm sm:text-base text-gray-600">Oda: {roomId}</p>
              <p className="text-sm sm:text-base text-gray-600">Katılımcı: {username}</p>
              <p className="text-sm sm:text-base text-gray-600">Toplam Katılımcı: {participantCount}</p>
            </div>
          </div>

          <div className="flex items-center gap-4 w-full sm:w-auto">
            {isVoting && (
              <div className="text-xl sm:text-2xl font-mono bg-white shadow rounded-lg p-3 sm:p-4 flex-1 sm:flex-none text-center">
                {formatTime(selectedCard as number)}
              </div>
            )}

            <button
              onClick={handleLeaveRoom}
              className="px-4 py-2 border border-transparent text-sm sm:text-base font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500 flex-1 sm:flex-none"
            >
              Ana Sayfaya Dön
            </button>
          </div>
        </div>

        {/* Main Content */}
        <div className="flex flex-col lg:grid lg:grid-cols-3 gap-4 sm:gap-8">
          {/* Cards Section */}
          <div className="lg:col-span-2 space-y-4 sm:space-y-8 order-2 lg:order-1">
            {isHost && !isVoting && (
              <button
                onClick={handleStartVoting}
                className="w-full px-4 sm:px-6 py-3 border border-transparent text-base sm:text-lg font-medium rounded-xl text-white bg-green-600 hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
              >
                Oylamayı Başlat
              </button>
            )}

            <div className="grid grid-cols-3 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-9 gap-2 sm:gap-3">
              {cards.map((card) => (
                <Card
                  key={card}
                  value={card}
                  isSelected={selectedCard === Number(card)}
                  onSelect={handleVote}
                />
              ))}
            </div>
          </div>

          {/* Participants Section */}
          <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6 order-1 lg:order-2">
            <div className="flex justify-between items-center mb-4">
              <h2 className="text-lg sm:text-xl font-semibold">Katılımcılar</h2>
              <span className="text-sm text-gray-500">
                {participants.length} / {participantCount}
              </span>
            </div>
            <div className="space-y-3 sm:space-y-4">
              {participants.map((participant) => (
                <div 
                  key={participant.id}
                  className={`
                    flex items-center justify-between p-2 sm:p-3 rounded-lg
                    ${participant.id === socket?.id ? 'bg-blue-50 ring-2 ring-blue-200' : 'bg-gray-50'}
                  `}
                >
                  <div className="flex items-center space-x-2 sm:space-x-3">
                    <div className="w-7 h-7 sm:w-8 sm:h-8 bg-blue-600 rounded-full flex items-center justify-center text-white font-semibold text-sm sm:text-base">
                      {participant.username.charAt(0).toUpperCase()}
                    </div>
                    <div>
                      <span className="font-medium text-sm sm:text-base">
                        {participant.username}
                      </span>
                      {participant.id === socket?.id && (
                        <span className="ml-2 text-xs bg-blue-100 text-blue-800 px-2 py-0.5 rounded-full">
                          Sen
                        </span>
                      )}
                    </div>
                  </div>
                  {participant.vote && (
                    <span className="px-2 sm:px-3 py-1 bg-blue-100 text-blue-800 rounded-full font-medium text-sm sm:text-base">
                      {participant.vote}
                    </span>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default Room;
