import React from 'react';

interface Participant {
  id: string;
  username: string;
  teamName: string;
  vote?: number;
  isHost?: boolean;
}

interface ParticipantListProps {
  participants: Participant[];
}

export const ParticipantList: React.FC<ParticipantListProps> = ({ participants }) => {
  return (
    <div className="space-y-2">
      {participants.map((participant) => (
        <div
          key={participant.id}
          className={`flex items-center justify-between p-3 bg-white rounded-xl shadow-sm hover:shadow-md transition-shadow duration-200 ${
            participant.isHost ? 'border-2 border-yellow-400 bg-yellow-50' : ''
          }`}
        >
          <div className="flex items-center space-x-3">
            <div className="relative">
              <div className={`w-10 h-10 rounded-lg flex items-center justify-center text-white font-medium shadow-sm ${
                participant.isHost 
                  ? 'bg-gradient-to-br from-yellow-500 to-yellow-600'
                  : 'bg-gradient-to-br from-sky-500 to-cyan-400'
              }`}>
                {participant.username.charAt(0).toUpperCase()}
              </div>
              {participant.isHost && (
                <div 
                  className="absolute -top-1 -right-1 w-5 h-5 bg-yellow-400 rounded-full border-2 border-white flex items-center justify-center"
                  title="Moderatör"
                >
                  <svg xmlns="http://www.w3.org/2000/svg" className="h-3 w-3 text-white" viewBox="0 0 20 20" fill="currentColor">
                    <path fillRule="evenodd" d="M10 2a1 1 0 011 1v1.323l3.954 1.582 1.599-.8a1 1 0 01.894 1.79l-1.233.616 1.738 5.42a1 1 0 01-.285 1.05A3.989 3.989 0 0115 15a3.989 3.989 0 01-2.667-1.019 1 1 0 01-.285-1.05l1.715-5.349L11 6.477V16h2a1 1 0 110 2H7a1 1 0 110-2h2V6.477L6.237 7.582l1.715 5.349a1 1 0 01-.285 1.05A3.989 3.989 0 015 15a3.989 3.989 0 01-2.667-1.019 1 1 0 01-.285-1.05l1.738-5.42-1.233-.616a1 1 0 01.894-1.79l1.599.8L9 4.323V3a1 1 0 011-1z" clipRule="evenodd" />
                  </svg>
                </div>
              )}
            </div>
            <div>
              <div className="font-medium text-gray-900 flex items-center gap-2">
                {participant.username}
                {participant.isHost && (
                  <span className="text-xs px-2 py-0.5 bg-yellow-100 text-yellow-800 rounded-full">
                    Moderatör
                  </span>
                )}
              </div>
              <div className="text-sm text-gray-500">
                {participant.teamName}
              </div>
            </div>
          </div>
          <div>
            {participant.vote !== undefined ? (
              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-sky-100 text-sky-800">
                {participant.vote}
              </span>
            ) : (
              <span className="text-sm text-gray-500">
                {participant.isHost ? "Moderatör oy kullanamaz" : "Henüz oy vermedi"}
              </span>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};
