import { useCallback } from 'react';

export const useSound = () => {
  const playCardSound = useCallback(() => {
    const audio = new Audio('/card-flip.mp3');
    audio.volume = 0.5; // Ses seviyesini yarıya indir
    audio.play().catch(() => {
      // Ses çalma hatalarını sessizce yakala
      console.log('Ses çalınamadı');
    });
  }, []);

  const playConfirmSound = useCallback(() => {
    const audio = new Audio('/confirm.mp3');
    audio.volume = 0.3; // Ses seviyesini biraz daha kıs
    audio.play().catch(() => {
      // Ses çalma hatalarını sessizce yakala
      console.log('Ses çalınamadı');
    });
  }, []);

  return { playCardSound, playConfirmSound };
};
