import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate, useLocation } from 'react-router-dom';
import Room from './components/Room';

function App() {
  const location = useLocation();
  const navigate = useNavigate();
  const [username, setUsername] = useState('');
  const [roomId, setRoomId] = useState('');
  const [participantCount, setParticipantCount] = useState(0);
  const [showParticipantInput, setShowParticipantInput] = useState(false);
  const [showUsernameInput, setShowUsernameInput] = useState(false);

  const handleCreateRoom = () => {
    const roomId = Math.random().toString(36).substring(2, 8).toUpperCase();
    setShowParticipantInput(true);
    setRoomId(roomId);
  };

  const handleConfirmParticipants = () => {
    if (participantCount > 0) {
      setShowParticipantInput(false);
      setShowUsernameInput(true);
    }
  };

  const handleJoinRoom = () => {
    if (roomId && username) {
      navigate(`/room/${roomId}`, { 
        state: { 
          username: username,
          participantCount: participantCount,
          isHost: true
        } 
      });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200">
      <Routes>
        <Route path="/" element={
          <div className="min-h-screen flex items-center justify-center px-4 sm:px-6 lg:px-8">
            <div className="max-w-md w-full space-y-8">
              <div className="text-center">
                <h1 className="text-5xl md:text-6xl font-bold">
                  <span className="text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600">
                    Scrum Poker
                  </span>
                </h1>
                <p className="mt-3 text-base text-gray-500 sm:mt-5 sm:text-lg sm:max-w-xl sm:mx-auto md:mt-5">
                  Takımınızla birlikte hızlı ve etkili eforlama yapın
                </p>
              </div>

              {!showParticipantInput && !showUsernameInput && (
                <div className="mt-8 space-y-6">
                  <button
                    onClick={handleCreateRoom}
                    className="group relative w-full flex justify-center py-4 px-6 border border-transparent text-lg font-medium rounded-xl text-white bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 transform transition-all duration-150 hover:scale-105 shadow-lg hover:shadow-xl"
                  >
                    <span className="absolute left-0 inset-y-0 flex items-center pl-3">
                      <svg className="h-5 w-5 text-blue-200 group-hover:text-blue-100" xmlns="http://www.w3.org/2000/svg" viewBox="0 0 20 20" fill="currentColor">
                        <path fillRule="evenodd" d="M10 3a1 1 0 011 1v5h5a1 1 0 110 2h-5v5a1 1 0 11-2 0v-5H4a1 1 0 110-2h5V4a1 1 0 011-1z" clipRule="evenodd" />
                      </svg>
                    </span>
                    Toplantı Oluştur
                  </button>

                  <div className="relative">
                    <div className="absolute inset-0 flex items-center">
                      <div className="w-full border-t border-gray-300"></div>
                    </div>
                    <div className="relative flex justify-center text-sm">
                      <span className="px-4 text-gray-500 bg-gradient-to-br from-gray-100 to-gray-200">
                        veya mevcut bir toplantıya katılın
                      </span>
                    </div>
                  </div>

                  <div className="rounded-xl bg-white shadow-lg hover:shadow-xl transition-shadow duration-300 p-6 space-y-4">
                    <div className="space-y-2">
                      <label htmlFor="room-id" className="block text-sm font-medium text-gray-700">
                        Toplantı ID'si
                      </label>
                      <input
                        id="room-id"
                        type="text"
                        placeholder="Örn: ABC123"
                        value={roomId}
                        onChange={(e) => setRoomId(e.target.value)}
                        className="appearance-none block w-full px-4 py-3 border border-gray-300 rounded-xl shadow-sm placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-lg"
                      />
                    </div>
                    <button
                      onClick={() => setShowUsernameInput(true)}
                      className="w-full flex justify-center py-3 px-6 border border-transparent text-lg font-medium rounded-xl text-white bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 transform transition-all duration-150 hover:scale-105 shadow-md hover:shadow-lg"
                    >
                      Toplantıya Katıl
                    </button>
                  </div>
                </div>
              )}

              {showParticipantInput && (
                <div className="mt-8 bg-white rounded-xl shadow-lg p-6 space-y-6">
                  <h2 className="text-2xl font-bold text-gray-900 text-center">
                    Katılımcı Sayısı
                  </h2>
                  <div className="space-y-4">
                    <div className="space-y-2">
                      <label htmlFor="participant-count" className="block text-sm font-medium text-gray-700">
                        Toplantıya katılacak kişi sayısı
                      </label>
                      <input
                        id="participant-count"
                        type="number"
                        min="1"
                        value={participantCount}
                        onChange={(e) => setParticipantCount(parseInt(e.target.value) || 0)}
                        className="appearance-none block w-full px-4 py-3 border border-gray-300 rounded-xl shadow-sm placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-lg"
                      />
                    </div>
                    <button
                      onClick={handleConfirmParticipants}
                      disabled={participantCount < 1}
                      className="w-full flex justify-center py-3 px-6 border border-transparent text-lg font-medium rounded-xl text-white bg-gradient-to-r from-blue-600 to-blue-700 hover:from-blue-700 hover:to-blue-800 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed transform transition-all duration-150 hover:scale-105 shadow-md hover:shadow-lg"
                    >
                      Devam Et
                    </button>
                  </div>
                </div>
              )}

              {showUsernameInput && (
                <div className="mt-8 bg-white rounded-xl shadow-lg p-6 space-y-6">
                  <h2 className="text-2xl font-bold text-gray-900 text-center">
                    Kullanıcı Adı
                  </h2>
                  <div className="space-y-4">
                    <div className="space-y-2">
                      <label htmlFor="username" className="block text-sm font-medium text-gray-700">
                        Toplantıda görünecek isminiz
                      </label>
                      <input
                        id="username"
                        type="text"
                        placeholder="Adınız"
                        value={username}
                        onChange={(e) => setUsername(e.target.value)}
                        className="appearance-none block w-full px-4 py-3 border border-gray-300 rounded-xl shadow-sm placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-blue-500 focus:border-transparent text-lg"
                      />
                    </div>
                    <button
                      onClick={handleJoinRoom}
                      disabled={!username.trim()}
                      className="w-full flex justify-center py-3 px-6 border border-transparent text-lg font-medium rounded-xl text-white bg-gradient-to-r from-green-500 to-green-600 hover:from-green-600 hover:to-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500 disabled:from-gray-400 disabled:to-gray-500 disabled:cursor-not-allowed transform transition-all duration-150 hover:scale-105 shadow-md hover:shadow-lg"
                    >
                      Toplantıya Katıl
                    </button>
                  </div>
                </div>
              )}
            </div>
          </div>
        } />
        <Route 
          path="/room/:roomId" 
          element={
            <Room 
              participantCount={location.state?.participantCount || 0} 
              isHost={location.state?.isHost || false} 
            />
          } 
        />
      </Routes>
    </div>
  );
}

export default function AppWrapper() {
  return (
    <Router>
      <App />
    </Router>
  );
}
