import React, { useMemo } from 'react';

interface Participant {
  id: string;
  username: string;
  vote?: number;
  isHost?: boolean;
}

interface ParticipantListProps {
  participants: Participant[];
}

const colors = [
  'bg-blue-100 text-blue-800',
  'bg-green-100 text-green-800',
  'bg-purple-100 text-purple-800',
  'bg-yellow-100 text-yellow-800',
  'bg-pink-100 text-pink-800',
  'bg-indigo-100 text-indigo-800',
  'bg-red-100 text-red-800',
  'bg-teal-100 text-teal-800'
];

export const ParticipantList: React.FC<ParticipantListProps> = ({ participants }) => {
  // Her katılımcı için sabit bir renk atama
  const participantColors = useMemo(() => {
    return participants.reduce((acc, participant) => {
      if (!acc[participant.id]) {
        acc[participant.id] = colors[Math.floor(Math.random() * colors.length)];
      }
      return acc;
    }, {} as Record<string, string>);
  }, [participants]);

  const getInitials = (name: string) => {
    return name
      .split(' ')
      .map(part => part[0])
      .join('')
      .toUpperCase()
      .slice(0, 2);
  };

  return (
    <div className="space-y-3">
      {participants.map((participant) => (
        <div
          key={participant.id}
          className="flex items-center justify-between p-3 bg-white rounded-lg border border-gray-100 hover:border-gray-200 transition-colors duration-200"
        >
          <div className="flex items-center space-x-3">
            <div className={`w-10 h-10 rounded-full flex items-center justify-center ${participantColors[participant.id]}`}>
              <span className="text-sm font-medium">
                {getInitials(participant.username)}
              </span>
            </div>
            <div className="flex flex-col">
              <span className="text-sm font-medium text-gray-900">
                {participant.username}
              </span>
              <span className="text-xs text-gray-500">
                {participant.isHost ? "Moderatör" : "Katılımcı"}
              </span>
            </div>
          </div>
          <div>
            {participant.vote !== undefined ? (
              <span className="inline-flex items-center px-3 py-1 rounded-full text-sm font-medium bg-blue-100 text-blue-800">
                {participant.vote}
              </span>
            ) : (
              <span className="text-sm text-gray-500">
                {participant.isHost ? "Moderatör oy kullanamaz" : "Henüz oy vermedi"}
              </span>
            )}
          </div>
        </div>
      ))}
    </div>
  );
};
