import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';

interface Participant {
  id: string;
  name: string;
  vote?: number;
}

const fibonacciNumbers = [1, 2, 3, 5, 8, 13, 21];

const Room = () => {
  const { roomId } = useParams<{ roomId: string }>();
  const navigate = useNavigate();
  const [participants, setParticipants] = useState<Participant[]>([]);
  const [username, setUsername] = useState('');
  const [isRevealed, setIsRevealed] = useState(false);
  const [isHost, setIsHost] = useState(false);

  useEffect(() => {
    // Burada server'dan oda bilgilerini alacağız
    // Şimdilik örnek veri kullanıyoruz
    setParticipants([
      { id: '1', name: 'John' },
      { id: '2', name: 'Alice' },
      { id: '3', name: 'Bob' },
    ]);
    setIsHost(true); // Bu değer server'dan gelecek
  }, [roomId]);

  const handleVote = (score: number) => {
    if (isRevealed) return;

    // Burada server'a oy gönderilecek
    setParticipants(prev =>
      prev.map(p =>
        p.id === '1' ? { ...p, vote: score } : p
      )
    );
  };

  const handleReveal = () => {
    setIsRevealed(true);
    // Burada server'a reveal isteği gönderilecek
  };

  const handleReset = () => {
    setIsRevealed(false);
    setParticipants(prev =>
      prev.map(p => ({ ...p, vote: undefined }))
    );
    // Burada server'a reset isteği gönderilecek
  };

  return (
    <div className="max-w-4xl mx-auto">
      <div className="bg-white shadow rounded-lg p-6">
        <div className="flex justify-between items-center mb-6">
          <div>
            <h2 className="text-2xl font-bold text-gray-900">Room: {roomId}</h2>
            <p className="text-gray-600">Share this room ID with your team</p>
          </div>
          <button
            onClick={() => navigate('/')}
            className="bg-gray-500 text-white px-4 py-2 rounded-md hover:bg-gray-600"
          >
            Leave Room
          </button>
        </div>

        <div className="grid grid-cols-1 md:grid-cols-2 gap-6">
          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4">Voting Cards</h3>
            <div className="grid grid-cols-3 gap-4">
              {fibonacciNumbers.map((number) => (
                <button
                  key={number}
                  onClick={() => handleVote(number)}
                  disabled={isRevealed}
                  className="aspect-square bg-indigo-500 text-white rounded-lg hover:bg-indigo-600 disabled:bg-gray-400 disabled:cursor-not-allowed transition-colors duration-200"
                >
                  {number}
                </button>
              ))}
            </div>
          </div>

          <div>
            <h3 className="text-lg font-medium text-gray-900 mb-4">Participants</h3>
            <div className="space-y-2">
              {participants.map((participant) => (
                <div
                  key={participant.id}
                  className="flex justify-between items-center p-3 bg-gray-50 rounded-lg"
                >
                  <span className="font-medium">{participant.name}</span>
                  {isRevealed ? (
                    <span className="text-lg font-bold text-indigo-600">
                      {participant.vote || 'No vote'}
                    </span>
                  ) : (
                    <span className="text-sm text-gray-500">
                      {participant.vote ? 'Voted' : 'Waiting'}
                    </span>
                  )}
                </div>
              ))}
            </div>
          </div>
        </div>

        {isHost && (
          <div className="mt-6 flex justify-end space-x-4">
            {!isRevealed ? (
              <button
                onClick={handleReveal}
                className="bg-green-500 text-white px-6 py-2 rounded-md hover:bg-green-600"
              >
                Reveal Votes
              </button>
            ) : (
              <button
                onClick={handleReset}
                className="bg-yellow-500 text-white px-6 py-2 rounded-md hover:bg-yellow-600"
              >
                Reset Room
              </button>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default Room;
