import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useNavigate } from 'react-router-dom';
import Room from './components/Room';

function Home() {
  const navigate = useNavigate();
  const [roomId, setRoomId] = useState('');
  const [showRoomId, setShowRoomId] = useState(false);
  const [username, setUsername] = useState('');
  const [showUsernameInput, setShowUsernameInput] = useState(false);

  const handleCreateRoom = () => {
    const newRoomId = Math.random().toString(36).substring(2, 8).toUpperCase();
    setRoomId(newRoomId);
    setShowRoomId(true);
    setShowUsernameInput(true);
  };

  const handleJoinRoom = () => {
    if (roomId.trim() && username.trim()) {
      navigate(`/room/${roomId}`, { state: { username } });
    }
  };

  return (
    <div className="min-h-screen bg-gradient-to-br from-gray-100 to-gray-200 flex items-center justify-center">
      <div className="bg-white p-8 rounded-lg shadow-xl max-w-md w-full">
        <h1 className="text-3xl font-bold text-transparent bg-clip-text bg-gradient-to-r from-blue-600 to-purple-600 text-center mb-8">
          Scrum Poker
        </h1>

        <div className="space-y-6">
          {!showRoomId ? (
            <button
              onClick={handleCreateRoom}
              className="w-full px-6 py-3 bg-blue-500 text-white rounded-lg hover:bg-blue-600 transition-colors duration-200 text-lg font-medium"
            >
              Toplantı Oluştur
            </button>
          ) : (
            <div className="space-y-4">
              <div className="bg-gray-50 p-4 rounded-lg">
                <p className="text-sm text-gray-600 mb-2">Toplantı ID'si:</p>
                <div className="flex items-center justify-between">
                  <code className="text-lg font-mono bg-white px-3 py-1 rounded border border-gray-200">
                    {roomId}
                  </code>
                  <button
                    onClick={() => {
                      navigator.clipboard.writeText(roomId);
                      alert('Toplantı ID\'si kopyalandı!');
                    }}
                    className="text-blue-500 hover:text-blue-600"
                  >
                    <svg xmlns="http://www.w3.org/2000/svg" className="h-5 w-5" viewBox="0 0 20 20" fill="currentColor">
                      <path d="M8 3a1 1 0 011-1h2a1 1 0 110 2H9a1 1 0 01-1-1z" />
                      <path d="M6 3a2 2 0 00-2 2v11a2 2 0 002 2h8a2 2 0 002-2V5a2 2 0 00-2-2 3 3 0 01-3 3H9a3 3 0 01-3-3z" />
                    </svg>
                  </button>
                </div>
              </div>

              {showUsernameInput && (
                <div className="space-y-2">
                  <label className="block text-sm font-medium text-gray-700">
                    Kullanıcı Adınız
                  </label>
                  <input
                    type="text"
                    value={username}
                    onChange={(e) => setUsername(e.target.value)}
                    placeholder="Kullanıcı adınızı girin"
                    className="w-full px-4 py-2 border border-gray-300 rounded-lg focus:ring-2 focus:ring-blue-500 focus:border-transparent"
                  />
                </div>
              )}

              <button
                onClick={handleJoinRoom}
                disabled={!username.trim()}
                className="w-full px-6 py-3 bg-green-500 text-white rounded-lg hover:bg-green-600 transition-colors duration-200 text-lg font-medium disabled:bg-gray-400 disabled:cursor-not-allowed"
              >
                Toplantıya Katıl
              </button>
            </div>
          )}
        </div>
      </div>
    </div>
  );
}

function App() {
  return (
    <Router>
      <Routes>
        <Route path="/" element={<Home />} />
        <Route path="/room/:roomId" element={<Room />} />
      </Routes>
    </Router>
  );
}

export default App;
