import React from 'react';
import { Participant } from '../types';

interface ParticipantListProps {
  participants: Participant[];
  currentUserId: string;
}

const getAvatarUrl = (name: string) => {
  return `https://ui-avatars.com/api/?name=${encodeURIComponent(name)}&background=random&color=fff&size=40`;
};

export const ParticipantList: React.FC<ParticipantListProps> = ({ participants, currentUserId }) => {
  return (
    <div className="bg-white rounded-lg shadow p-4">
      <h3 className="text-lg font-semibold mb-4">Katılımcılar</h3>
      <div className="space-y-3">
        {participants.map((participant) => (
          <div
            key={participant.id}
            className={`flex items-center space-x-3 p-2 rounded-lg ${
              participant.id === currentUserId ? 'bg-blue-50' : 'bg-gray-50'
            }`}
          >
            <img
              src={getAvatarUrl(participant.name)}
              alt={participant.name}
              className="w-10 h-10 rounded-full"
            />
            <div className="flex-1">
              <p className="font-medium">{participant.name}</p>
              <p className="text-sm text-gray-500">
                {participant.id === currentUserId ? '(Sen)' : ''}
              </p>
            </div>
            {participant.hasVoted && (
              <span className="text-green-500">
                <svg
                  xmlns="http://www.w3.org/2000/svg"
                  className="h-5 w-5"
                  viewBox="0 0 20 20"
                  fill="currentColor"
                >
                  <path
                    fillRule="evenodd"
                    d="M10 18a8 8 0 100-16 8 8 0 000 16zm3.707-9.293a1 1 0 00-1.414-1.414L9 10.586 7.707 9.293a1 1 0 00-1.414 1.414l2 2a1 1 0 001.414 0l4-4z"
                    clipRule="evenodd"
                  />
                </svg>
              </span>
            )}
          </div>
        ))}
      </div>
    </div>
  );
};
