import React from 'react';

interface CardProps {
  value: number;
  isSelected: boolean;
  onSelect: (value: number) => void;
}

export function Card({ value, isSelected, onSelect }: CardProps) {
  return (
    <button
      onClick={() => onSelect(value)}
      className={`
        min-h-[80px] sm:min-h-[100px] aspect-[3/4] flex items-center justify-center text-2xl sm:text-3xl font-bold rounded-xl shadow-lg
        transform transition-all duration-150 active:scale-95 hover:scale-105
        ${isSelected
          ? 'bg-blue-600 text-white ring-2 ring-blue-600 ring-offset-2'
          : 'bg-white text-gray-900 hover:bg-gray-50'
        }
      `}
    >
      {value}
    </button>
  );
}
