import React, { useEffect, useState, useCallback } from 'react';
import { useLocation, useNavigate } from 'react-router-dom';
import { useSocket } from '../hooks/useSocket';
import { Card } from '../components/Card';
import { ParticipantList } from '../components/ParticipantList';
import { Notification } from '../components/Notification';
import { RoomHeader } from '../components/RoomHeader';
import { VotePopup } from '../components/VotePopup';

interface LocationState {
  roomId: string;
  username: string;
  isHost: boolean;
  participantCount: number;
}

interface EffortItem {
  id: string;
  title: string;
  description: string;
  status: 'pending' | 'voting' | 'completed';
  votes: Record<string, number>;
}

function Room() {
  const location = useLocation();
  const navigate = useNavigate();
  const { socket, isConnected } = useSocket();
  const [participants, setParticipants] = useState<Array<{ id: string; username: string; vote?: number; isHost?: boolean }>>([]);
  const [notifications, setNotifications] = useState<Array<{ id: number; message: string; type: 'success' | 'error' }>>([]);
  const [selectedCard, setSelectedCard] = useState<number | null>(null);
  const [isHost, setIsHost] = useState(false);
  const [username, setUsername] = useState('');
  const [roomId, setRoomId] = useState('');
  const [showVotePopup, setShowVotePopup] = useState(false);
  const [pendingVote, setPendingVote] = useState<number | null>(null);
  const [effortItems, setEffortItems] = useState<EffortItem[]>([]);
  const [newEffortTitle, setNewEffortTitle] = useState('');
  const [newEffortDescription, setNewEffortDescription] = useState('');
  const [showNewEffortForm, setShowNewEffortForm] = useState(false);

  useEffect(() => {
    const state = location.state as LocationState;
    if (!state?.roomId || !state?.username) {
      navigate('/');
      return;
    }

    setRoomId(state.roomId);
    setUsername(state.username);
    setIsHost(state.isHost);
  }, [location, navigate]);

  const addNotification = useCallback((message: string, type: 'success' | 'error') => {
    const id = Date.now();
    setNotifications(prev => [...prev, { id, message, type }]);
    setTimeout(() => {
      setNotifications(prev => prev.filter(n => n.id !== id));
    }, 3000);
  }, []);

  useEffect(() => {
    if (!socket || !roomId || !username) return;

    // Odaya katılma
    if (!isConnected) {
      socket.emit('joinRoom', { roomId, username });
    }

    // Katılımcı listesi güncelleme
    const handleParticipantsUpdate = (updatedParticipants: Array<{ id: string; username: string; vote?: number; isHost?: boolean }>) => {
      setParticipants(updatedParticipants);
    };

    // Yeni katılımcı bildirimi
    const handleNewParticipant = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıya katıldı`, 'success');
    };

    // Katılımcı ayrılma bildirimi
    const handleParticipantLeft = (participant: { username: string }) => {
      addNotification(`${participant.username} toplantıdan ayrıldı`, 'error');
    };

    // Oy güncelleme
    const handleVoteUpdate = (data: { username: string; vote: number }) => {
      console.log('Oy güncellendi:', data);
      setParticipants(prev => 
        prev.map(p => 
          p.username === data.username 
            ? { ...p, vote: data.vote }
            : p
        )
      );
    };

    // Eforlama listesi güncelleme
    const handleEffortItemsUpdate = (updatedEffortItems: EffortItem[]) => {
      console.log('Eforlama listesi güncellendi:', updatedEffortItems);
      setEffortItems(updatedEffortItems);
    };

    // Event listener'ları ekle
    socket.on('participantsUpdate', handleParticipantsUpdate);
    socket.on('newParticipant', handleNewParticipant);
    socket.on('participantLeft', handleParticipantLeft);
    socket.on('voteUpdate', handleVoteUpdate);
    socket.on('effortItemsUpdate', handleEffortItemsUpdate);

    // Cleanup
    return () => {
      socket.off('participantsUpdate', handleParticipantsUpdate);
      socket.off('newParticipant', handleNewParticipant);
      socket.off('participantLeft', handleParticipantLeft);
      socket.off('voteUpdate', handleVoteUpdate);
      socket.off('effortItemsUpdate', handleEffortItemsUpdate);
    };
  }, [socket, roomId, username, isConnected, addNotification]);

  const handleVote = useCallback((value: number) => {
    if (!socket) return;

    setPendingVote(value);
    setShowVotePopup(true);
  }, [socket]);

  const handleConfirmVote = useCallback(() => {
    if (!socket || !pendingVote) return;

    console.log('Oy verildi:', {
      username,
      vote: pendingVote
    });

    setSelectedCard(pendingVote);
    socket.emit('vote', {
      roomId,
      username,
      vote: pendingVote
    });

    setShowVotePopup(false);
    setPendingVote(null);
  }, [socket, roomId, username, pendingVote]);

  const handleLeaveRoom = useCallback(() => {
    if (socket) {
      socket.emit('leaveRoom', { roomId, username });
    }
    navigate('/');
  }, [socket, roomId, username, navigate]);

  const handleAddEffort = useCallback(() => {
    if (!socket || !newEffortTitle.trim()) return;

    const newEffort: EffortItem = {
      id: Date.now().toString(),
      title: newEffortTitle,
      description: newEffortDescription,
      status: 'pending',
      votes: {}
    };

    socket.emit('addEffort', {
      roomId,
      effort: newEffort
    });

    setNewEffortTitle('');
    setNewEffortDescription('');
    setShowNewEffortForm(false);
  }, [socket, roomId, newEffortTitle, newEffortDescription]);

  const handleStartVoting = useCallback((effortId: string) => {
    if (!socket) return;

    socket.emit('startVoting', {
      roomId,
      effortId
    });
  }, [socket, roomId]);

  const handleShowResults = useCallback((effortId: string) => {
    if (!socket) return;

    socket.emit('showResults', {
      roomId,
      effortId
    });
  }, [socket, roomId]);

  const handleResetVoting = useCallback((effortId: string) => {
    if (!socket) return;

    socket.emit('resetVoting', {
      roomId,
      effortId
    });
  }, [socket, roomId]);

  useEffect(() => {
    if (!socket || !roomId || !username) return;

    // Yeni eforlama ekleme
    const handleNewEffort = (effort: EffortItem) => {
      setEffortItems(prev => [...prev, effort]);
      addNotification('Yeni eforlama eklendi', 'success');
    };

    // Eforlama güncelleme
    const handleEffortUpdate = (updatedEffort: EffortItem) => {
      setEffortItems(prev => 
        prev.map(effort => 
          effort.id === updatedEffort.id ? updatedEffort : effort
        )
      );
    };

    // Event listener'ları ekle
    socket.on('newEffort', handleNewEffort);
    socket.on('effortUpdate', handleEffortUpdate);

    // Cleanup
    return () => {
      socket.off('newEffort', handleNewEffort);
      socket.off('effortUpdate', handleEffortUpdate);
    };
  }, [socket, roomId, username, addNotification]);

  const cards = [1, 2, 3, 5, 8, 13, 21, 34, 55];

  return (
    <div className="min-h-screen bg-gray-100 py-6 sm:py-12">
      <div className="max-w-7xl mx-auto px-4 sm:px-6 lg:px-8">
        <div className="fixed top-4 right-4 z-50 space-y-2">
          {notifications.map(notification => (
            <Notification
              key={notification.id}
              message={notification.message}
              type={notification.type}
            />
          ))}
        </div>

        <div className="grid grid-cols-1 lg:grid-cols-3 gap-6">
          {/* Header */}
          <div className="lg:col-span-3">
            <RoomHeader
              roomId={roomId}
              username={username}
              isHost={isHost}
            />
          </div>

          {/* Cards Section */}
          <div className="lg:col-span-3 space-y-4 sm:space-y-8">
            {/* Katılımcı Listesi - Sadece moderatör için */}
            {isHost && (
              <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg sm:text-xl font-semibold text-gray-900">
                    Katılımcılar
                  </h3>
                  <button
                    onClick={() => {
                      const pendingEffort = effortItems.find(effort => effort.status === 'pending');
                      if (pendingEffort) {
                        handleStartVoting(pendingEffort.id);
                      } else {
                        addNotification('Oylamaya başlanacak eforlama bulunamadı', 'error');
                      }
                    }}
                    className="px-4 py-2 text-sm font-medium text-white bg-green-600 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
                  >
                    Oylamayı Başlat
                  </button>
                </div>
                <ParticipantList participants={participants} />
              </div>
            )}

            {/* Eforlama Listesi - Sadece moderatör için */}
            {isHost && (
              <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6">
                <div className="flex justify-between items-center mb-4">
                  <h3 className="text-lg sm:text-xl font-semibold text-gray-900">
                    Eforlamalar
                  </h3>
                  <button
                    onClick={() => setShowNewEffortForm(true)}
                    className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                  >
                    Yeni Eforlama Ekle
                  </button>
                </div>

                {showNewEffortForm && (
                  <div className="mb-4 p-4 bg-gray-50 rounded-lg">
                    <input
                      type="text"
                      value={newEffortTitle}
                      onChange={(e) => setNewEffortTitle(e.target.value)}
                      placeholder="Eforlama Başlığı"
                      className="w-full mb-2 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                    <textarea
                      value={newEffortDescription}
                      onChange={(e) => setNewEffortDescription(e.target.value)}
                      placeholder="Eforlama Açıklaması"
                      className="w-full mb-2 px-3 py-2 border border-gray-300 rounded-md focus:outline-none focus:ring-2 focus:ring-blue-500"
                    />
                    <div className="flex justify-end space-x-2">
                      <button
                        onClick={() => setShowNewEffortForm(false)}
                        className="px-4 py-2 text-sm font-medium text-gray-700 bg-gray-100 rounded-md hover:bg-gray-200 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-gray-500"
                      >
                        İptal
                      </button>
                      <button
                        onClick={handleAddEffort}
                        className="px-4 py-2 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                      >
                        Ekle
                      </button>
                    </div>
                  </div>
                )}

                <div className="space-y-4">
                  {effortItems.map((effort) => (
                    <div key={effort.id} className="p-4 bg-gray-50 rounded-lg">
                      <h4 className="font-medium text-gray-900">{effort.title}</h4>
                      {effort.description && (
                        <p className="text-sm text-gray-600 mt-1">{effort.description}</p>
                      )}
                      <div className="mt-4 flex justify-between items-center">
                        <span className="text-sm text-gray-500">
                          Durum: {effort.status === 'pending' ? 'Bekliyor' : effort.status === 'voting' ? 'Oylama' : 'Tamamlandı'}
                        </span>
                        <div className="space-x-2">
                          {effort.status === 'pending' && (
                            <button
                              onClick={() => handleStartVoting(effort.id)}
                              className="px-3 py-1 text-sm font-medium text-white bg-green-600 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
                            >
                              Oylamayı Başlat
                            </button>
                          )}
                          {effort.status === 'voting' && (
                            <button
                              onClick={() => handleShowResults(effort.id)}
                              className="px-3 py-1 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
                            >
                              Sonuçları Göster
                            </button>
                          )}
                          {effort.status === 'completed' && (
                            <button
                              onClick={() => handleResetVoting(effort.id)}
                              className="px-3 py-1 text-sm font-medium text-white bg-yellow-600 rounded-md hover:bg-yellow-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500"
                            >
                              Sıfırla
                            </button>
                          )}
                        </div>
                      </div>
                    </div>
                  ))}
                </div>
              </div>
            )}

            {/* Fibonacci Kartları - Sadece katılımcılar için */}
            {!isHost && (
              <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6">
                {/* Aktif Eforlama Bilgisi */}
                {effortItems.length > 0 && (
                  <div className="mb-6">
                    <h3 className="text-lg font-semibold text-gray-900 mb-2">
                      Aktif Eforlama
                    </h3>
                    {effortItems.map((effort) => (
                      effort.status === 'voting' && (
                        <div key={effort.id} className="p-4 bg-blue-50 rounded-lg border border-blue-200">
                          <h4 className="font-medium text-blue-900">{effort.title}</h4>
                          {effort.description && (
                            <p className="text-sm text-blue-700 mt-1">{effort.description}</p>
                          )}
                          <div className="mt-2 text-sm text-blue-600">
                            Lütfen bu talebi eforlamak için Fibonacci kartlarından birini seçin
                          </div>
                        </div>
                      )
                    ))}
                  </div>
                )}

                {/* Fibonacci Kartları - Sadece aktif oylama varsa göster */}
                {effortItems.some(effort => effort.status === 'voting') ? (
                  <div className="grid grid-cols-3 sm:grid-cols-3 md:grid-cols-5 lg:grid-cols-9 gap-2 sm:gap-3">
                    {cards.map((card) => (
                      <Card
                        key={card}
                        value={card}
                        isSelected={selectedCard === card}
                        onSelect={handleVote}
                      />
                    ))}
                  </div>
                ) : (
                  <div className="text-center py-8">
                    <p className="text-gray-500">
                      Oylama başlatılmasını bekliyor...
                    </p>
                  </div>
                )}
              </div>
            )}

            {/* Toplantıdan Ayrıl Butonu */}
            <div className="flex justify-end">
              <button
                onClick={handleLeaveRoom}
                className="px-4 py-2 text-sm font-medium text-white bg-red-600 rounded-md hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
              >
                Toplantıdan Ayrıl
              </button>
            </div>
          </div>
        </div>

        <VotePopup
          isOpen={showVotePopup}
          onClose={() => {
            setShowVotePopup(false);
            setPendingVote(null);
          }}
          onConfirm={handleConfirmVote}
          value={pendingVote || 0}
        />
      </div>
    </div>
  );
}

export default Room;
