import React from 'react';

interface Participant {
  id: string;
  username: string;
  vote?: number;
}

interface ParticipantListProps {
  participants: Participant[];
  isHost: boolean;
  onStartVoting: () => void;
  onShowResults: () => void;
  onResetVoting: () => void;
  onLeaveRoom: () => void;
}

export function ParticipantList({
  participants,
  isHost,
  onStartVoting,
  onShowResults,
  onResetVoting,
  onLeaveRoom,
}: ParticipantListProps) {
  return (
    <div className="bg-white rounded-xl shadow-lg p-4 sm:p-6 space-y-4">
      <div className="flex items-center justify-between">
        <h2 className="text-lg sm:text-xl font-semibold text-gray-900">
          Katılımcılar ({participants.length})
        </h2>
        {isHost && (
          <div className="flex gap-2">
            <button
              onClick={onStartVoting}
              className="px-3 py-1 text-sm font-medium text-white bg-green-600 rounded-md hover:bg-green-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-green-500"
            >
              Başlat
            </button>
            <button
              onClick={onShowResults}
              className="px-3 py-1 text-sm font-medium text-white bg-blue-600 rounded-md hover:bg-blue-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-blue-500"
            >
              Sonuçlar
            </button>
            <button
              onClick={onResetVoting}
              className="px-3 py-1 text-sm font-medium text-white bg-yellow-600 rounded-md hover:bg-yellow-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-yellow-500"
            >
              Sıfırla
            </button>
          </div>
        )}
      </div>

      <div className="space-y-2">
        {participants.map((participant) => (
          <div
            key={participant.id}
            className="flex items-center justify-between p-2 bg-gray-50 rounded-lg"
          >
            <span className="text-sm sm:text-base font-medium text-gray-900">
              {participant.username}
            </span>
            {participant.vote !== undefined && (
              <span className="px-2 sm:px-3 py-1 bg-blue-100 text-blue-800 rounded-full font-medium text-sm sm:text-base">
                {participant.vote}
              </span>
            )}
          </div>
        ))}
      </div>

      <button
        onClick={onLeaveRoom}
        className="w-full px-4 py-2 border border-transparent text-sm font-medium rounded-md text-white bg-red-600 hover:bg-red-700 focus:outline-none focus:ring-2 focus:ring-offset-2 focus:ring-red-500"
      >
        Toplantıdan Ayrıl
      </button>
    </div>
  );
}
